INSERT INTO main.email_template ("name", body) VALUES ('new_user', '<!DOCTYPE html> <html lang=''es'' xmlns=''https://www.w3.org/1999/xhtml''> <head> <meta charset=''UTF-8''> <meta content=''width=device-width,initial-scale=1'' name=''viewport''> <meta name=''x-apple-disable-message-reformatting''> <title>Mail - New User Place or Farm Store</title> <!--[if mso]> <noscript> <xml> <o:OfficeDocumentSettings> <o:PixelsPerInch>96</o:PixelsPerInch> </o:OfficeDocumentSettings> </xml> </noscript> <![endif]--> </head> <body style=''margin:0; padding:0;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0;border-spacing:0; background:#ffffff; font-family: Tahoma, sans-serif;''> <tr> <td align=''center'' style=''margin: 0; padding:0; font-family: Tahoma, sans-serif;''> <table role=''presentation'' style=''margin:0; padding: 0; width:602px; border-collapse:collapse; border-spacing:0; text-align:center; font-family: Tahoma, sans-serif;''> <tr> <td style=''padding:32px; background: #F5F5F5; border-radius: 16px;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0; border-spacing:0; text-align: left;''> <tr> <td style=''margin: 0; background: #FFFFFF; border-radius: 16px; padding: 24px;''> <h1 style=''font-weight:700; font-size:24px; margin:0 0 8px 0; color:#656A77; font-family: Tahoma, sans-serif;''> ¡Hola! person_name </h1> <h2 style=''font-weight:700; font-size:20px; margin:0 0 16px 0; font-family: Tahoma, sans-serif; color:#656A77;''> Nuevo usuario de <a style="color: rgba(1,125,63,1);">Sound</a><a style=''color: rgb(241, 207, 105);''>Good</a></h2>  <p style=''margin:0 0 18px 0; font-size:16px; line-height:24px; font-family: Tahoma, sans-serif; color:#656A77;''>SoundGood ha generado un nuevo usuario <strong>{charge}</strong> para tí. Puede encontrar detalles sobre su cuenta a continuación: </p> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> </p> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> <strong style=''color: rgba(1,125,63,1);''>Usuario: </strong>user_name </p> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> </p> <!-- <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> <strong style=''color: rgb(241, 207, 105);''>Contraseña: </strong>user_password </p> --> <a href=''redirect'' style=''display: inline-block; text-decoration: none; min-width: 100px; padding: 12px 28px; border-radius: 4px; background-color: rgba(1,125,63,1); color: #FFFFFF; font-size: 16px; font-family: Roboto, sans-serif; font-weight: 700;''> Ingresar a la plataforma </a> </td> </tr> <tr> <tr> <td style=''width:100%; padding:8px 0 8px 10px; vertical-align:top;''> <p style=''margin:0; font-size:16px; line-height:20px; font-family: Tahoma, sans-serif; color:#656A77;''> <strong style=''color: rgba(1,125,63,1);''> ¡Gracias por unirte a nosotros! </strong> Si tiene alguna duda, <a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''> háganoslo saber. </a> </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Por favor no responder a este correo directamente, ya que esta casilla de correo no es revisada. </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> &copy;current_year <strong> <a href=''redirect'' style=''color: rgba(1,125,63,1); text-decoration:none;''>Sound</a><a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''>Good</a></strong> - Todos los derechos reservados </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Ocaña - Colombia </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> </table> </body> </html>');
INSERT INTO main.email_template ("name", body) VALUES ('two_factor_authentication', '<!DOCTYPE html> <html lang=''es'' xmlns=''https://www.w3.org/1999/xhtml''> <head> <meta charset=''UTF-8''> <meta content=''width=device-width,initial-scale=1'' name=''viewport''> <meta name=''x-apple-disable-message-reformatting''> <title>Mail - Two factor authentication</title> <!--[if mso]> <noscript> <xml> <o:OfficeDocumentSettings> <o:PixelsPerInch>96</o:PixelsPerInch> </o:OfficeDocumentSettings> </xml> </noscript> <![endif]--> </head> <body style=''margin:0; padding:0;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0;border-spacing:0; background:#ffffff; font-family: Tahoma, sans-serif;''> <tr> <td align=''center'' style=''margin: 0; padding:0; font-family: Tahoma, sans-serif;''> <table role=''presentation'' style=''margin:0; padding: 0; width:602px; border-collapse:collapse; border-spacing:0; text-align:center; font-family: Tahoma, sans-serif;''> <tr> <td style=''padding:32px; background: #F5F5F5; border-radius: 16px;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0; border-spacing:0; text-align: left;''> <tr> <td style=''margin: 0; background: #FFFFFF; border-radius: 16px; padding: 24px;''> <h1 style=''font-weight:700; font-size:24px; margin:0 0 8px 0; color:#656A77; font-family: Tahoma, sans-serif;''> ¡Hola! person_name </h1> <h3 style=''margin-top:36px; font-size:18px; line-height:24px; font-weight: 700; color:#656A77; font-family: Roboto, sans-serif; margin-bottom: 16px;''> Codigo de verificación </h3> <p style=''margin:0 0 18px 0; font-size:16px; line-height:24px; font-family: Tahoma, sans-serif; color:#656A77;''>SoundGood ha generado tu código de verificación para iniciar sesión: </p> <div style=''text-align: center;''> <span style=''display: inline-block; text-decoration: none; min-width: 100px; padding: 12px 28px; border-radius: 4px; background-color: rgba(1,125,63,1); color: #FFFFFF; font-size: 16px; font-family: Roboto, sans-serif; font-weight: 700; text-align: center;''>{codigo}</span> </div> </td> </tr> <tr> <tr> <td style=''width:100%; padding:8px 0 8px 10px; vertical-align:top;''> <p style=''margin:0; font-size:15px; line-height:20px; font-family: Tahoma, sans-serif; color:#656A77;''>Si no fue usted, cambie su contraseña para proteger su cuenta. <a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''> aquí </a> </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Por favor no responder a este correo directamente, ya que esta casilla de correo no es revisada. </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> &copy;current_year <strong> <a href=''redirect'' style=''color: rgba(1,125,63,1); text-decoration:none;''>Sound</a><a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''>Good</a></strong> - Todos los derechos reservados </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Ocaña - Colombia </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> </table> </body> </html>');
INSERT INTO main.email_template ("name", body) VALUES ('recovery_password', '<!DOCTYPE html> <html lang=''es'' xmlns=''https://www.w3.org/1999/xhtml''> <head> <meta charset=''UTF-8''> <meta content=''width=device-width,initial-scale=1'' name=''viewport''> <meta name=''x-apple-disable-message-reformatting''> <title>Mail - Recovery Password</title> <!--[if mso]> <noscript> <xml> <o:OfficeDocumentSettings> <o:PixelsPerInch>96</o:PixelsPerInch> </o:OfficeDocumentSettings> </xml> </noscript> <![endif]--> </head> <body style=''margin:0; padding:0;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0;border-spacing:0; background:#ffffff; font-family: Tahoma, sans-serif;''> <tr> <td align=''center'' style=''margin: 0; padding:0; font-family: Tahoma, sans-serif;''> <table role=''presentation'' style=''margin:0; padding: 0; width:602px; border-collapse:collapse; border-spacing:0; text-align:center; font-family: Tahoma, sans-serif;''> <tr> <td style=''padding:32px; background: #F5F5F5; border-radius: 16px;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0; border-spacing:0; text-align: left;''> <tr> <td style=''margin: 0; background: #FFFFFF; border-radius: 16px; padding: 24px;''> <h1 style=''font-weight:700; font-size:24px; margin:0 0 8px 0; color:#656A77; font-family: Tahoma, sans-serif;''> <a style=''color: rgba(1,125,63,1);'' >¡Hola!</a> person_name </h1> <h2 style=''font-weight:700; font-size:18px; margin:0 0 16px 0; font-family: Tahoma, sans-serif; color:#656A77;''> Estas intentando recuperar tu <a style=''color: rgb(241, 207, 105);''>contraseña</a></h2> <p style=''margin:0 0 18px 0; font-size:16px; line-height:24px; font-family: Tahoma, sans-serif; color:#656A77;''>Hemos recibido una solicitud para restablecer tu contraseña en nuestra plataforma, deberá hacer clic en el siguiente botón.</p> <a href=''redirect'' style=''display: inline-block; text-decoration: none; min-width: 100px; padding: 12px 28px; border-radius: 4px; background-color: rgba(1,125,63,1); color: #FFFFFF; font-size: 16px; font-family: Roboto, sans-serif; font-weight: 700;''> Recuperar contraseña </a></td> </tr> <tr> <td style=''width:100%; padding:8px 0 8px 10px; vertical-align:top;''><p style=''margin:0; font-size:15px; line-height:20px; font-family: Tahoma, sans-serif; color:#656A77;''>Si tiene problemas para hacer clic en el botón ''Recuperar contraseña'', copie y pegue la siguiente URL en su navegador web: <a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''> link_recovery_password </a> </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Por favor no responder a este correo directamente, ya que esta casilla de correo no es revisada. </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> &copy;current_year <strong> <a href=''redirect'' style=''color: rgba(1,125,63,1); text-decoration:none;''>Sound</a><a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''>Good</a></strong> - Todos los derechos reservados </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Ocaña - Colombia </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> </table> </body> </html>');
INSERT INTO main.email_template ("name", body) VALUES ('change_password', '<!DOCTYPE html> <html lang=''es'' xmlns=''https://www.w3.org/1999/xhtml''> <head> <meta charset=''UTF-8''> <meta content=''width=device-width,initial-scale=1'' name=''viewport''> <meta name=''x-apple-disable-message-reformatting''> <title>Mail - Change Password</title> <!--[if mso]> <noscript> <xml> <o:OfficeDocumentSettings> <o:PixelsPerInch>96</o:PixelsPerInch> </o:OfficeDocumentSettings> </xml> </noscript> <![endif]--> </head> <body style=''margin:0; padding:0;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0;border-spacing:0; background:#ffffff; font-family: Tahoma, sans-serif;''> <tr> <td align=''center'' style=''margin: 0; padding:0; font-family: Tahoma, sans-serif;''> <table role=''presentation'' style=''margin:0; padding: 0; width:602px; border-collapse:collapse; border-spacing:0; text-align:center; font-family: Tahoma, sans-serif;''> <tr> <td style=''padding:32px; background: #F5F5F5; border-radius: 16px;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0; border-spacing:0; text-align: left;''> <tr> <td style=''margin: 0; background: #FFFFFF; border-radius: 16px; padding: 24px;''> <h1 style=''font-weight:700; font-size:24px; margin:0 0 8px 0; color:#656A77; font-family: Tahoma, sans-serif;''> <a style=''color: rgba(1,125,63,1);'' >¡Hola!</a> person_name </h1> <h2 style=''font-weight:700; font-size:18px; margin:0 0 16px 0; font-family: Tahoma, sans-serif; color:#656A77;''> Tu contraseña ha sido cambiada</h2>  <p style=''margin:0 0 18px 0; font-size:16px; line-height:24px; font-family: Tahoma, sans-serif; color:#656A77;''>La contraseña para su cuenta autorizada acaba de cambiarse. Si fue usted, puede ignorar este correo electrónico de manera segura. </p> </td> </tr> <tr> <td style=''width:100%; padding:8px 0 8px 10px; vertical-align:top;''><p style=''margin:0; font-size:15px; line-height:20px; font-family: Tahoma, sans-serif; color:#656A77;''>Si no fuiste tú, tu cuenta ha sido comprometida. Por favor intenta <a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''> recuperar la contraseña. </a> </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Por favor no responder a este correo directamente, ya que esta casilla de correo no es revisada. </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> &copy;current_year <strong> <a href=''redirect'' style=''color: rgba(1,125,63,1); text-decoration:none;''>Sound</a><a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''>Good</a></strong> - Todos los derechos reservados </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Ocaña - Colombia </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> </table> </body> </html>');
INSERT INTO main.email_template ("name", body) VALUES ('reset_user_password_admin', '<!DOCTYPE html> <html lang=''es'' xmlns=''https://www.w3.org/1999/xhtml''> <head> <meta charset=''UTF-8''> <meta content=''width=device-width,initial-scale=1'' name=''viewport''> <meta name=''x-apple-disable-message-reformatting''> <title>Mail - Reset Password By Admin</title> <!--[if mso]> <noscript> <xml> <o:OfficeDocumentSettings> <o:PixelsPerInch>96</o:PixelsPerInch> </o:OfficeDocumentSettings> </xml> </noscript> <![endif]--> </head> <body style=''margin:0; padding:0;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0;border-spacing:0; background:#ffffff; font-family: Tahoma, sans-serif;''> <tr> <td align=''center'' style=''margin: 0; padding:0; font-family: Tahoma, sans-serif;''> <table role=''presentation'' style=''margin:0; padding: 0; width:602px; border-collapse:collapse; border-spacing:0; text-align:center; font-family: Tahoma, sans-serif;''> <tr> <td style=''padding:32px; background: #F5F5F5; border-radius: 16px;''> <table role=''presentation'' style=''width:100%; border-collapse:collapse; border:0; border-spacing:0; text-align: left;''> <tr> <td style=''margin: 0; background: #FFFFFF; border-radius: 16px; padding: 24px;''> <h1 style=''font-weight:700; font-size:24px; margin:0 0 8px 0; color:#656A77; font-family: Tahoma, sans-serif;''> <a style=''color: rgba(1,125,63,1);'' >¡Hola!</a> person_name </h1>  <p style=''margin:0 0 18px 0; font-size:16px; line-height:24px; font-family: Tahoma, sans-serif; color:#656A77;''>Hemos recibido la solicitud de que presentas problemas con la contraseña para iniciar sesión en nuestra plataforma.</p> <h2 style=''font-weight:700; font-size:16px; margin:0 0 16px 0; font-family: Tahoma, sans-serif; color:#656A77;''> Estas son tus nuevas <a style=''color: rgb(241, 207, 105);''>credenciales</a></h2> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> <strong style=''color: rgba(1,125,63,1);''>Usuario: </strong>user_name </p> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> </p> <p style=''color: #656A77; font-family: Tahoma, sans-serif; margin: 10px 0;''> <strong style=''color: rgb(241, 207, 105);''>Contraseña: </strong>user_password </p> <a href=''redirect'' style=''display: inline-block; text-decoration: none; min-width: 100px; padding: 12px 28px; border-radius: 4px; background-color: rgba(1,125,63,1); color: #FFFFFF; font-size: 16px; font-family: Roboto, sans-serif; font-weight: 700;''>Ingresar a la plataforma</a></td> </tr> <tr> <td style=''width:100%; padding:8px 0 8px 10px; vertical-align:top;''><p style=''margin:0; font-size:15px; line-height:20px; font-family: Tahoma, sans-serif; color:#656A77;''> Si no eres tú quien realizo esta solicitud, omite este correo o comunicate con el administrador. </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Por favor no responder a este correo directamente, ya que esta casilla de correo no es revisada. </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> &copy;current_year <strong> <a href=''redirect'' style=''color: rgba(1,125,63,1); text-decoration:none;''>Sound</a><a href=''redirect'' style=''color: rgb(241, 207, 105); text-decoration:none;''>Good</a></strong> - Todos los derechos reservados </p> </td> </tr> <tr> <td> <p style=''margin:8px 0 8px 0; font-size:14px; line-height:16px; color:#384461; font-family: Tahoma, sans-serif; text-align: center;''> Ocaña - Colombia </p> </td> </tr> </table> </td> </tr> </table> </td> </tr> </table> </body> </html>');